INSERT INTO `reci2` VALUES ('196', '2G', '2G，第二代手机通信技术规格，以数字语音传输技术为核心。用户体验速率为10kbps，峰值速率为100kbps。一般定义为无法直接传送如电子邮件、软件等信息；只具有通话和一些如时间日期等传送的手机通信技术规格。不过手机短信在它的某些规格中能够被执行。它在美国通常称为“个人通讯服务”（外语缩写：PCS）。', 'https://baike.baidu.com/item/2G', '手机');
INSERT INTO `reci2` VALUES ('197', '待机时间', '科技名词，待机时间指手机完全充满电后，插卡开机合上盖，不进行任何操作（即不打、不接电话、不玩游戏等），直至自动关机的时间。', 'https://baike.baidu.com/item/%E5%BE%85%E6%9C%BA%E6%97%B6%E9%97%B4', '手机');
