INSERT INTO `reci2` VALUES ('1822', 'PDF417', 'PDF417条码是一种高密度、高信息含量的便携式数据文件，是实现证件及卡片等大容量、高可靠性信息自动存储、携带并可用机器自动识读的理想手段。', 'https://baike.baidu.com/item/PDF417', '二维码');
