INSERT INTO `reci2` VALUES ('1926', '区块链信息服务管理规定', '《区块链信息服务管理规定》是经国家互联网信息办公室室务会议审议通过的管理规定 [1]  。 2019年1月10日，国家互联网信息办公室发布《区块链信息服务管理规定》，自2019年2月15日起施行。', 'https://baike.baidu.com/item/%E5%8C%BA%E5%9D%97%E9%93%BE%E4%BF%A1%E6%81%AF%E6%9C%8D%E5%8A%A1%E7%AE%A1%E7%90%86%E8%A7%84%E5%AE%9A/23245975', '区块链');
